local CityHeroStateBase = require "DataCenter.CityResident.CityHeroState.CityHeroStateBase"
local CityHeroStateFire = BaseClass("CityHeroStateFire", CityHeroStateBase)

local function OnEnter(self)
    local targetData = self.data.target
    if targetData then
        self.data:LookAt(targetData:GetPos())
        self.data:ShowHeroTorch()
        self.data:Idle()
        self.data:PlayAnim(CityResidentDefines.AnimName.Ignite)
        self.data:WaitForFinish(1)
    else
        self.data:Refresh()
    end
end

local function OnFinish(self)
    local targetData = self.data.target
    if targetData then
        targetData:BeFire()
    end
    self.data:SetState(CityResidentDefines.HeroState.GoToIdle)
end

CityHeroStateFire.OnEnter = OnEnter
CityHeroStateFire.OnFinish = OnFinish

return CityHeroStateFire