-- 专精技能：坚盾
local OpBuildingShield = BaseClass("OpBuildingShield", OpMasterySkillBase)
local base = OpMasterySkillBase

local function Init(self, uuid)
    self.skill = MasterySkill.BuildingShield
    base.Init(self, uuid)
end

local function OnClick(self, view, item)
    if self.state == MasterySkillState.Normal then
        DataCenter.MasteryManager:TryUseSkill(self.skill, { bUuid = self.buildData.uuid }, function()
            view:Close()
        end)
    elseif self.state == MasterySkillState.Locked then
        DataCenter.MasteryManager:ShowHome(self.template.home, self.template.group)
    end
end

OpBuildingShield.Init = Init
OpBuildingShield.OnClick = OnClick

return OpBuildingShield