local HeroLackTipsItemBase = require "DataCenter.HeroLackTipManager.HeroLackTipsItemBase"
local HeroLackTipsItemActivity = BaseClass("HeroLackTipsItemActivity", HeroLackTipsItemBase)

local function CheckIsOk(self)
    local template = DataCenter.HeroLackTipTemplateManager:GetTemplate(self.configId)
    local data = DataCenter.ActivityListDataManager:GetActivityDataById(template.para1)
    if data and DataCenter.ActivityListDataManager:CheckIsSend(data) then
        return true
    end
    return false
end

local function TodoAction(self)
    local template = DataCenter.HeroLackTipTemplateManager:GetTemplate(self.configId)
    UIManager.Instance:DestroyWindow(UIWindowNames.UIHeroList)
    UIManager.Instance:DestroyWindow(UIWindowNames.UIHeroInfo)
    UIManager.Instance:DestroyWindow(UIWindowNames.UIHeroAdvanceSuccess)
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIActivityCenterTable, { anim = true, UIMainAnim = UIMainAnimType.AllHide, hideTop = true }, template.para1)
end

HeroLackTipsItemActivity.CheckIsOk = CheckIsOk
HeroLackTipsItemActivity.TodoAction = TodoAction

return HeroLackTipsItemActivity