local ResLackItemBase = require "DataCenter.ResLackTips.ResLackItemBase"
local ResLackItem_BuyGiftPackageByRechargeId = BaseClass("ResLackItem_BuyGiftPackageByRechargeId", ResLackItemBase)
local Localization = CS.GameEntry.Localization

function ResLackItem_BuyGiftPackageByRechargeId:CheckIsOk( _resType, _needCnt)
    if  CS.SceneManager.IsInPVE() then
        return false
    end
    local str = string.split(self._config:getValue("para1"),";")
    local data = WelfareController.getShowTagInfos()
    if data and next(data) then
        for index, v in ipairs(data) do
            for k = 1 ,table.count(str) do
                local type = GetTableData("recharge", tonumber(str[k]), "type")
                if type == v:getType() then
                    self.WelfareType = v:getType()
                    return true
                end
            end
        end
    end
    return false
end

function ResLackItem_BuyGiftPackageByRechargeId:TodoAction()
    if self._config == nil then
        return
    end
    GoToUtil.CloseAllWindows()
    GoToUtil.GotoGiftPackView(nil,self.WelfareType)
end

return ResLackItem_BuyGiftPackageByRechargeId