local ResLackItemBase = require "DataCenter.ResLackTips.ResLackItemBase"
local ResLackItem_GoActWin = BaseClass("ResLackItem_GoActWin", ResLackItemBase)

function ResLackItem_GoActWin:CheckIsOk( _resType, _needCnt )
    local para1 = self._config:getValue("para1")
    local list = string.split(para1,"|")
    for i = 1 ,table.count(list) do
        local actListData = DataCenter.ActivityListDataManager:GetActivityDataById(list[i])
        if actListData then
            if DataCenter.ActivityListDataManager:CheckIsSend(actListData) then
                self.actId = tonumber(list[i])
                return true
            end
        end
    end
    return false
end

function ResLackItem_GoActWin:TodoAction()
    GoToUtil.GoActWindow(self.actId)
end

return ResLackItem_GoActWin


