--[[
跳转赛季任务
]]

local ResLackItemBase = require "DataCenter.ResLackTips.ResLackItemBase"
local ResLackItem_GoSeasonTask = BaseClass("ResLackItem_GoSeasonTask", ResLackItemBase)

function ResLackItem_GoSeasonTask:CheckIsOk( _resType, _needCnt )
    local seasonList = DataCenter.TaskManager:GetSeasonChapterTask()
    if SeasonUtil.IsInSeasonDesertMode() and seasonList and table.count(seasonList) > 0 then
        for i = 1 ,table.count(seasonList) do
            if seasonList[i].state == TaskState.NoComplete  then
                return true
            end
        end
    end
    return false
end

function ResLackItem_GoSeasonTask:TodoAction()
    GoToUtil.CloseAllWindows()
    UIManager:GetInstance():OpenWindow(UIWindowNames.UITaskMain, HideBlurPanelAnim, TaskType.Main)
end

return ResLackItem_GoSeasonTask
