local ResLackItemBase = require "DataCenter.ResLackTips.ResLackItemBase"
local ResLackItem_Quest = BaseClass("ResLackItem_Quest", ResLackItemBase)

function ResLackItem_Quest:CheckIsOk( _resType, _needCnt )
    return true
end

function ResLackItem_Quest:TodoAction()
    local taskType = 0
    local showMainTask = DataCenter.TaskManager:CheckShowMainTask()
    local allChapterNum =  DataCenter.ChapterTaskManager:GetAllNum()
    if allChapterNum > 0 then   -- 有章节任务
        taskType = TaskType.Chapter
    elseif showMainTask then
        taskType = TaskType.Main
    else
        taskType = TaskType.Daily
    end

    GoToUtil.CloseAllWindows()
    UIManager:GetInstance():OpenWindow(UIWindowNames.UITaskMain, HideBlurPanelAnim, taskType)
end

return ResLackItem_Quest