function ResLackItem_SeasonGroundManage:CheckIsOk( _resType, _needCnt)
    if  CS.SceneManager.IsInPVE() then
        return false
    end
    local list = {}
    local tempList = DataCenter.DesertDataManager:GetSortList(true)
    if tempList~=nil then
        for k,v in pairs(tempList) do
            list[v] = 1
        end
    end
    local dic = DataCenter.DesertDataManager:GetDesertDataForShow()
    for k,v in pairs(dic) do
        if list[v.level] == 1 then
            if v.giveUpTime and v.giveUpTime == 0 then
                return true
            end
   
        end
    end
    return false
end

