local WeaponSkillBarCell = BaseClass("WeaponSkillBarCell", UIBaseContainer)
local base = UIBaseContainer

local UILWTacticalWeaponItem = require "UI.UILWTacticalWeapon.Component.UILWTacticalWeaponItem" 
local NORMAL_SCALE = 0.8
local TANK_HEIGHT = Vector3.New(0,80,0)
local Resource = CS.GameEntry.Resource
local baseNode = "Content/ZombieBattleHeroCell/"

function WeaponSkillBarCell:__init()
    
end

function WeaponSkillBarCell:__delete()
    self:OnDestroy()

    if(self.req) then
        self.req:Destroy()
        self.req = nil
    end
    self.target = nil
    self.gameObject = nil
    self.transform = nil
end

function WeaponSkillBarCell:Load(member, transform, height)
    self.member = member
    self.target = transform
    self.camera = CS.UnityEngine.Camera.main
    self.height = height
    if height < 0 then
        self.height = 1
    end
    self.myWorldPos = Vector3.zero

    self.req = Resource:InstantiateAsync("Assets/Main/Prefabs/LWBattle/WeaponSkillBar.prefab")
    self.req:completed('+', function(req)
        local go = req.gameObject
        local CanvasNormal = UIManager:GetInstance():GetLayer(UILayer["Scene"]["Name"]).gameObject
        go.transform:SetParent(CanvasNormal.transform)
        self.gameObject = go
        self.transform = go.transform
        self.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)

        self.__var_arg = self.gameObject
        self:OnCreate()

        self:SetData(self.member)
    end)
end

function WeaponSkillBarCell:OnCreate()
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

function WeaponSkillBarCell:OnDestroy()
    self:DataDestroy()
    self:ComponentDestroy()
    base.OnDestroy(self)
end

function WeaponSkillBarCell:ComponentDefine()
    self.bg = self:AddComponent(UIBaseComponent, baseNode.."Bg")
    self.animator = self:AddComponent(UIAnimator, baseNode.."Bg")
    self.animator:Play("Eff_ui_jinengshifang_default",0,0)--重置一下
    self.cdImg = self:AddComponent(UIImage, baseNode.."Bg/UILWTacticalWeaponItem/cd")
    self.lock = self:AddComponent(UIBaseComponent, baseNode.."Bg/UILWTacticalWeaponItem/skill/lock")
    self.skillImg = self:AddComponent(UIImage, baseNode.."Bg/UILWTacticalWeaponItem/skill/skillImg")
    self.skillImg2 = self:AddComponent(UIImage, baseNode.."Eff_UI_jinengshifang/Image/Image")
    self.ring = self:AddComponent(UIBaseComponent, baseNode.."Bg/UILWTacticalWeaponItem/ring")
    self.ring:SetActive(false)
    self.skillNode = self:AddComponent(UIBaseComponent, baseNode.."Bg/UILWTacticalWeaponItem/skill")
    self.skillNode:SetActive(false)
    self.cdText = self:AddComponent(UIText, baseNode.."Bg/UILWTacticalWeaponItem/cdText")
    self.icon = self:AddComponent(UILWTacticalWeaponItem, baseNode.."Bg/UILWTacticalWeaponItem")
    self.skillBubble = self:AddComponent(UIAnimator, baseNode.."Eff_UI_jinengshifang")
    self.skillBubble:SetActive(false)
    CS.UIGray.SetGray(self.icon.transform, false, true)
end

function WeaponSkillBarCell:ComponentDestroy()
    self.icon=nil
    self.bg=nil
    self.cdImg=nil
    self.lock=nil
    self.ring=nil
    self.cdText=nil
    self.animator=nil
    self.skillBubble =nil
end

function WeaponSkillBarCell:DataDefine()
    self.timeStopDuration=0
    self.skillCD=1
end

function WeaponSkillBarCell:DataDestroy()
    if self.loopAnim then
        self.loopAnim:Stop()
        self.loopAnim=nil
    end
    if self.updateTimer ~= nil then
        UpdateManager:GetInstance():RemoveUpdate(self.updateTimer)
        self.updateTimer = nil
    end
    self.member=nil
    self.skill=nil
    self.fire = false
end

function WeaponSkillBarCell:OnUpdate()

    if self.skill and (not self.skill.lock) then
        local curCD,maxCD=self.skill:GetCurAndMaxCD()

        if curCD>0.4 then
            self.cdImg:SetFillAmount(curCD/maxCD)
            self.cdText:SetText(string.format("%.1fs", curCD))
        elseif curCD>0 then--curCD在(0，0.4]区间内，马上cd转好
            self.cdImg:SetFillAmount(curCD/maxCD)
            self.cdText:SetText(string.format("%.1fs", curCD))

            if self.skillCD>0.4 then
                self.animator:Play("Eff_ui_jinengshifang_tubiaojineng_daiji",0,0)
                self.loopAnim = TimerManager:GetInstance():DelayInvoke(function()
                    self.animator:Play("Eff_ui_jinengshifang_tubiao_kuang_daiji",0,0)
                end, 0.5)
            end

        else--curCD<=0
            self.cdImg:SetFillAmount(0)
            self.cdText:SetText("")

        end
        self.skillCD=curCD
        local skillState = self.skill.state
        local skillFire = true
        if skillState == SkillCastState.Cooldown or skillState == SkillCastState.Ready then
            skillFire = false
        end

        if skillFire ~= self.fire then
            self.fire = skillFire
            if skillFire then
                --检测到技能释放
                self:OnFireState()
            end
        end

        if self.timeStopDuration>0 then
            self.timeStopDuration = self.timeStopDuration - Time.deltaTime
            self:SetBubblePos()
        end
    end

    if self.transform then
        self:UpdatePos()
    end
end

function WeaponSkillBarCell:UpdatePos()

    -- 获取对应位置在屏幕上的坐标位置
    self.myWorldPos.x, self.myWorldPos.y, self.myWorldPos.z = self.target:Get_position()
    self.myWorldPos.y = self.myWorldPos.y + self.height
    self.transform.position = CS.CSUtils.WorldPositionToUISpacePosition(self.myWorldPos)
    
end

function WeaponSkillBarCell:OnEnable()
    base.OnEnable(self)
end

function WeaponSkillBarCell:OnDisable()
    base.OnDisable(self)
    if self.updateTimer ~= nil then
        UpdateManager:GetInstance():RemoveUpdate(self.updateTimer)
        self.updateTimer = nil
    end
end

function WeaponSkillBarCell:OnAddListener()
    base.OnAddListener(self)
end

function WeaponSkillBarCell:OnRemoveListener()
    base.OnRemoveListener(self)
end

---@param member Scene.LWBattle.ParkourBattle.Team.WeaponUnit
function WeaponSkillBarCell:SetData(member)
    self.fire = false
    if member then
        self.icon:SetActive(true)
        self.member=member---@type Scene.LWBattle.ParkourBattle.Team.WeaponUnit
        ---@type table<number,Scene.LWBattle.Skill.Skill>
        local allActiveSkills = member.skillManager:GetAllActiveSkills()
        for i, v in pairs(allActiveSkills) do
            --获取非普攻的技能
            if (not self.skill) then
                self.skill = v
                break
            end
        end
        
        local appearanceId = nil
        if member.appearanceMeta then
            appearanceId = member.appearanceMeta.id
        end
        self.icon:SetData(member.weaponData,appearanceId)
        self.bg:SetLocalScaleXYZ(NORMAL_SCALE,NORMAL_SCALE,NORMAL_SCALE)
        self.cdImg:SetFillAmount(0)
        self.cdText:SetText("")
        if self.skill then
            self.skillImg:LoadSprite(self.skill.meta.icon)
            self.skillImg2:LoadSprite(self.skill.meta.icon)
        end

        self:HideLock()
        
        if not self.updateTimer then
            self.updateTimer = function() self:OnUpdate() end
            UpdateManager:GetInstance():AddUpdate(self.updateTimer)
        end
    else
        self.icon:SetActive(false)
    end
end


function WeaponSkillBarCell:OnRefreshUltimateLock()
    if self.member then
        if self.member:UltimateIsLock() then
            if self.member:UltimateCanUnlock() then
                self:ShowCanUnlock()
            else
                self:ShowCantUnlock()
            end
        else
            self:HideLock()
        end
    end
end

--隐藏锁定
function WeaponSkillBarCell:HideLock()
    self.lock:SetActive(false)
end

--技能释放表现
function WeaponSkillBarCell:OnFireState()
    if self.loopAnim then
        self.loopAnim:Stop()
        self.loopAnim=nil
    end
    self.animator:Play("Eff_ui_jinengshifang_tubiaojineng",0,0)
    DataCenter.LWSoundManager:PlaySound(10016)

    self.timeStopDuration = self.skill.meta.time_stop_duration>0 and self.skill.meta.time_stop_duration or 3
    self:SetBubblePos()
    self.skillBubble:SetActive(true)
    self.skillBubble:SetLocalScaleXYZ(0.4,0.4,0.4)
    self.skillBubble:Play("Eff_ui_jinengshifang_show",0,0)
end

function WeaponSkillBarCell:SetBubblePos()
    local worldPos = self.member:GetPosition()
    local skillWorldPos = CS.CSUtils.WorldPositionToUISpacePosition(worldPos) + TANK_HEIGHT
    self.skillBubble:SetPosition(skillWorldPos)
end

return WeaponSkillBarCell
