local EnterGameState = BaseClass("EnterGameState", LoadingStateBase)

function EnterGameState:__init(startupLoading)
    LoadingStateBase:__init(startupLoading)
end

function EnterGameState:OnEnter(args)
    LoadingPrint("OnEnterGame")
    if self._startupLoading.isCanCloseLoading == true or not DataCenter.GuideManager:IsStartId() then
        Logger.Log("[loading] close loading begin")
        self._startupLoading:CloseUILoading()
        SoundUtil.PlayMainSceneBGMusic()
        CS.GameEntry.GlobalData:recordGaid();
        CS.GameEntry.Sdk:LogEvent("app_launch")
        Logger.Log("[loading]on fire load_complete")
        EventManager:GetInstance():Broadcast(EventId.LOAD_COMPLETE)
        Logger.Log("[loading]on finish fire load_complete")
        EventManager:GetInstance():Broadcast(EventId.Guide_video_Play)
        Logger.Log("[loading]on finish fire Guide_video_Play")
    end
end

