local AccountDeleteVerifyDeviceMessage = BaseClass("AccountDeleteVerifyDeviceMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization


local function OnCreate(self,type,verifyCode)
    base.OnCreate(self)
    self.sfsObj:PutInt("type",type)--0 登录邮箱验证；1删除设备验证
    if verifyCode~=nil then
        self.sfsObj:PutUtfString("verifyCode",verifyCode)
    end
end

local function HandleMessage(self, message)
    base.HandleMessage(self, message)
    if message["errorCode"] ~= nil then
        local lang = Localization:GetString(message["errorCode"])
        UIUtil.ShowTips(lang or message["errorCode"])
        return
    else
        if message["type"] == 1 then
            UIManager:GetInstance():DestroyWindow(UIWindowNames.UIMailVerify)
            UIUtil.ShowTipsId(120175)
        end
        
    end
end

AccountDeleteVerifyDeviceMessage.OnCreate = OnCreate
AccountDeleteVerifyDeviceMessage.HandleMessage = HandleMessage

return AccountDeleteVerifyDeviceMessage