local AccountVerifyDeviceMessage = BaseClass("AccountVerifyDeviceMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization


local function OnCreate(self,mail,type,verifyCode)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("mail",mail)
    self.sfsObj:PutInt("type",type)
    if verifyCode~=nil then
        self.sfsObj:PutUtfString("verifyCode",verifyCode)
    end
    
end

local function HandleMessage(self, message)
    base.HandleMessage(self, message)
    if message["errorCode"] ~= nil then
        local lang = Localization:GetString(message["errorCode"])
        UIUtil.ShowTips(lang or message["errorCode"])
        return
    else
        if message["type"]~=nil then
            local type = message["type"]
            if type ==0 then
                local mailStr = message["mail"]
            elseif type == 1 then
                UIManager:GetInstance():DestroyWindow(UIWindowNames.UIMailVerify)
                UIManager:GetInstance():DestroyWindow(UIWindowNames.UIMailAuthenticate)
                UIManager:GetInstance():DestroyWindow(UIWindowNames.UIMailAuthenticateReal)
                CS.ApplicationLaunch.Instance.Loading:OnAuthSuccess()
            end
        end
    end
end

AccountVerifyDeviceMessage.OnCreate = OnCreate
AccountVerifyDeviceMessage.HandleMessage = HandleMessage

return AccountVerifyDeviceMessage