local ActivityEventRankRewardMessage = BaseClass("ActivityEventRankRewardMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,actId)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("actId", actId)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        if t["actId"]~=nil and t["actId"]~="" then
            DataCenter.StrongestCommanderDataManager:ParseEventRewardInfo(t)
            EventManager:GetInstance():Broadcast(EventId.MsgFreshSingleScoreRankView)
        end
    end
end

ActivityEventRankRewardMessage.OnCreate = OnCreate
ActivityEventRankRewardMessage.HandleMessage = HandleMessage

return ActivityEventRankRewardMessage