local ActivityTodayPersonalRankMessage = BaseClass("ActivityTodayPersonalRankMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,actId)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("actId", actId)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.StrongestCommanderDataManager:ParseTodayRankData(t)
        EventManager:GetInstance():Broadcast(EventId.ZONE_CONTRIBUTE_RANK_UPDATE)
    end
end

ActivityTodayPersonalRankMessage.OnCreate = OnCreate
ActivityTodayPersonalRankMessage.HandleMessage = HandleMessage

return ActivityTodayPersonalRankMessage