local BarterShopExchangeMessage = BaseClass("BarterShopExchangeMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self, activityId, id, count)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("activityId", tostring(activityId))
    self.sfsObj:PutUtfString("id", tostring(id))
    self.sfsObj:PutInt("count", count)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["gold"] then
            LuaEntry.Player.gold = t["gold"]
            EventManager:GetInstance():Broadcast(EventId.UpdateGold)
        end
        if t["reward"] ~= nil then
            for k,v in pairs(t["reward"]) do
                DataCenter.RewardManager:AddOneReward(v)
            end
        end
        DataCenter.ActivityListDataManager:UpdateActBarterShopInfo(t)
    end
end

BarterShopExchangeMessage.OnCreate = OnCreate
BarterShopExchangeMessage.HandleMessage = HandleMessage

return BarterShopExchangeMessage