local SendGiftByActivityMessage = BaseClass("SendGiftByActivityMessage", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self,activityId, id, uid, notice, message)
    base.OnCreate(self)
    self.sfsObj:PutInt("activityId", toInt(activityId))
    self.sfsObj:PutInt("notice", notice)
    self.sfsObj:PutUtfString("id", tostring(id))
    self.sfsObj:PutUtfString("toUid", uid)
    self.sfsObj:PutUtfString("content", message)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["gold"] then
            LuaEntry.Player.gold = t["gold"]
            EventManager:GetInstance():Broadcast(EventId.UpdateGold)
        end
        DataCenter.ActivityListDataManager:UpdateActBarterShopInfo(t)
    end

end

SendGiftByActivityMessage.OnCreate = OnCreate
SendGiftByActivityMessage.HandleMessage = HandleMessage

return SendGiftByActivityMessage