local PushSevenDayActScoreMessage = BaseClass("PushSevenDayActScoreMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,activityId)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        local actInfo = DataCenter.ActivityListDataManager:GetActivityDataById(tostring(t["activityId"]))
        if actInfo then
            if actInfo.type == EnumActivity.HeroGrowth.Type then
                DataCenter.HeroGrowthActivityManager:OnRecvGrowthScorePush(t)
            else
                DataCenter.ActSevenDayData:UpdateDayActScore(t)
            end
        end
        
    end
end

PushSevenDayActScoreMessage.OnCreate = OnCreate
PushSevenDayActScoreMessage.HandleMessage = HandleMessage

return PushSevenDayActScoreMessage