local AlDismissMessage = BaseClass("AlDismissMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.AllianceBaseDataManager:ResetAllianceData()
		EventManager:GetInstance():Broadcast(EventId.AllianceQuitOK)
		-- 清除信息之后再更新
		if t["lastUpdateTime"] then
			LuaEntry.Player:SetLastUpdateTime(t["lastUpdateTime"])
		end
    end
end

AlDismissMessage.OnCreate = OnCreate
AlDismissMessage.HandleMessage = HandleMessage

return AlDismissMessage