local AlLeaderElectSignUpMessage = BaseClass("AlLeaderElectSignUpMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self, slogan)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("declaration", slogan)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        local baseInfo = DataCenter.AllianceBaseDataManager:GetAllianceBaseData()
        if baseInfo then
            baseInfo:UpdateRegister(1)
        end
        DataCenter.AlLeaderElectManager:AddOrUpdateOneCandidate(t)
    end
end

AlLeaderElectSignUpMessage.OnCreate = OnCreate
AlLeaderElectSignUpMessage.HandleMessage = HandleMessage

return AlLeaderElectSignUpMessage