local AllianceLeaderElectMessage = BaseClass("AllianceLeaderElectMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.AllianceBaseDataManager:UpdateLeaderElectStatus(t.candidate)
        EventManager:GetInstance():Broadcast(EventId.AlLeaderElectStatusChange)
    end
end

AllianceLeaderElectMessage.OnCreate = OnCreate
AllianceLeaderElectMessage.HandleMessage = HandleMessage

return AllianceLeaderElectMessage