local AllianceLeaderReplaceMessage = BaseClass("AllianceLeaderReplaceMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,playerId)
    base.OnCreate(self)
    self.sfsObj:PutUtfString("playerId", playerId)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode = t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
		local Player = LuaEntry.Player
        if t["alliance"]~=nil then
            DataCenter.AllianceBaseDataManager:UpdateAllianceBaseData(t)
        end
        if t["oldLeaderId"]~=nil then
			local uid = Player.uid
            DataCenter.AllianceMemberDataManager:AllianceLeaderChange(t["oldLeaderId"], uid, t["oldLeaderRank"])
        end
        if t["gold"] ~= nil then
            Player.gold = t["gold"]
            EventManager:GetInstance():Broadcast(EventId.UpdateGold)
        end
        EventManager:GetInstance():Broadcast(EventId.AllianceMember)
        UIUtil.ShowTipsId(390193) 
    end
end
AllianceLeaderReplaceMessage.OnCreate = OnCreate
AllianceLeaderReplaceMessage.HandleMessage = HandleMessage

return AllianceLeaderReplaceMessage