local AllianceRewardHideNameMessage = BaseClass("AllianceRewardHideNameMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,openHide)
    base.OnCreate(self)
    self.sfsObj:PutInt("openHide", openHide)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        if t["alGiftHideName"]~=nil then
            LuaEntry.Player.alGiftHideName = t["alGiftHideName"]
        end
        EventManager:GetInstance():Broadcast(EventId.AlGiftHideNameStateUpdate)
        
        local tipID = t["alGiftHideName"] == 1 and "390809" or "390808"
        UIUtil.ShowTipsId(tipID) 
    end
end

AllianceRewardHideNameMessage.OnCreate = OnCreate
AllianceRewardHideNameMessage.HandleMessage = HandleMessage

return AllianceRewardHideNameMessage