local AllianceShowHelpMessage = BaseClass("AllianceShowHelpMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.AllianceHelpDataManager:UpdateHelpInfoList(t)
        EventManager:GetInstance():Broadcast(EventId.AllianceHelpSever)
        EventManager:GetInstance():Broadcast(EventId.UpdateAllianceHelpNum)
    end

end

AllianceShowHelpMessage.OnCreate = OnCreate
AllianceShowHelpMessage.HandleMessage = HandleMessage

return AllianceShowHelpMessage