--- 新联盟日志推送
local PushAllianceLogMemberMessage = BaseClass("PushAllianceLogMemberMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, message)
    base.HandleMessage(self, message)
    if message["errorCode"] ~= nil then
        UIUtil.ShowTips(Localization:GetString(message["errorCode"]))
        return
    end
    local logMsg = message["logMsg"]
    if logMsg ~= nil then
        local logType = logMsg["type"]
        if DataCenter.AllianceCityLogManager:IsAllianceCityLogType(logType) then
            DataCenter.AllianceCityLogManager:PushAllianceLogMemberHandle(logMsg)
        else
            DataCenter.AllianceMainManager:PushAllianceLogMemberHandle(logMsg)
        end
    end
end

PushAllianceLogMemberMessage.OnCreate = OnCreate
PushAllianceLogMemberMessage.HandleMessage = HandleMessage

return PushAllianceLogMemberMessage