local BuyArenaTicketMessage = BaseClass("BuyArenaTicketMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        if t.remainGold then
            LuaEntry.Player.gold = t.remainGold
            EventManager:GetInstance():Broadcast(EventId.UpdateGold)
        end
        if t.item then
            DataCenter.ItemData:UpdateOneItem(t.item)
        end
        if t.buyTime then
            DataCenter.ArenaManager:UpdateBaseInfo(t)
        end
    end
end

BuyArenaTicketMessage.OnCreate = OnCreate
BuyArenaTicketMessage.HandleMessage = HandleMessage

return BuyArenaTicketMessage