local BuyItemAndResourceMessage = BaseClass("BuyItemAndResourceMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self, res,item)
    base.OnCreate(self)
    if res then
        local resources = SFSArray.New()
        for k,v in pairs(res) do
            local obj = SFSObject.New()
            obj:PutInt("type",k)
            obj:PutInt("value", v)
            resources:AddSFSObject(obj)
        end
        self.sfsObj:PutSFSArray("resources", resources)
    end
    if item then
        local items = SFSArray.New()
        for k,v in pairs(item) do
            local obj = SFSObject.New()
            obj:PutUtfString("itemId",k)
            obj:PutInt("value", v)
            items:AddSFSObject(obj)
        end
        self.sfsObj:PutSFSArray("items", items)
    end
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["remainGold"]~=nil then
            LuaEntry.Player.gold = t["remainGold"]
            EventManager:GetInstance():Broadcast(EventId.UpdateGold)
        end
        if t["resource"] then
            LuaEntry.Resource:UpdateResource(t["resource"])
        end
        EventManager:GetInstance():Broadcast(EventId.BuyItemAndRes)
    end
end

BuyItemAndResourceMessage.OnCreate = OnCreate
BuyItemAndResourceMessage.HandleMessage = HandleMessage

return BuyItemAndResourceMessage
