local CrossGetAlliancePointMessage = BaseClass("CrossGetAlliancePointMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
end
local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["server"]~=nil and t["pointId"]~=nil then
            local serverId = t["server"] 
            local pointId = t["pointId"]
            local str = serverId..";"..pointId
            if serverId == LuaEntry.Player:GetSelfServerId() then
                if pointId == 0 then
                    UIUtil.ShowTipsId(110224)
                    return
                end
            end
            EventManager:GetInstance():Broadcast(EventId.CrossServerAlliancePoint,str)
        end
    end
end

