local DispatchRewardMessage = BaseClass("DispatchRewardMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

function DispatchRewardMessage:OnCreate(uuid)
    base.OnCreate(self)
    self.sfsObj:PutLong("uuid", uuid)
end

function DispatchRewardMessage:HandleMessage(message)
    base.HandleMessage(self, message)
    local errCode =  message["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        ----- 创建回家行军
        --if message["endPointId"] then
        --    DataCenter.ActDispatchTaskFakeMarchManager:AddMarchIndex(message.endPointId, message.startPointId, true)
        --end
        if message["reward"] then
            DataCenter.RewardManager:ShowCommonReward(message)
            for k,v in pairs(message["reward"]) do
                DataCenter.RewardManager:AddOneReward(v)
            end
        end
        if message["deleteUuid"] then
            DataCenter.ActDispatchTaskDataManager:DeleteSingleTasks({message["deleteUuid"]})
        end
        if message["mission"] then
            DataCenter.ActDispatchTaskDataManager:UpdateOneSingleTask(message["mission"], true)
        end
        if message["newMissions"]~=nil then
            local arr = message["newMissions"]
            for _, v in ipairs(arr) do
                DataCenter.ActDispatchTaskDataManager:UpdateOneSingleTask(v, false)
            end
            EventManager:GetInstance():Broadcast(EventId.DispatchTaskUpdateSingle)
        end
    end
end

return DispatchRewardMessage