local FormationSaveMessage = BaseClass("FormationSaveMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self,index,formations,heroInfos)
    base.OnCreate(self)
    self.sfsObj:PutInt("index", index)
    
    local formationArray = SFSArray.New()
    table.walk(formations,function (k,v)
        local obj = SFSObject.New()
        obj:PutUtfString("armyId",k)
        obj:PutInt("count", v)
        formationArray:AddSFSObject(obj)
    end)
    
    self.sfsObj:PutSFSArray("formations", formationArray)
    
    local heroArray = SFSArray.New()
    table.walk(heroInfos,function (k,v)
        local obj = SFSObject.New()
        obj:PutInt("heroUuid",v)
        obj:PutInt("index", k)
        heroArray:AddSFSObject(obj)
    end)
    
    self.sfsObj:PutSFSArray("heroInfos", heroArray)
    
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.ArmyFormationDataManager:InitArmyFormationListData(t)
        UIUtil.ShowTipsId(300056) 
    end
end

FormationSaveMessage.OnCreate = OnCreate
FormationSaveMessage.HandleMessage = HandleMessage

return FormationSaveMessage