local GetHeroMedalShopInfoMessage = BaseClass("GetHeroMedalShopInfoMessage", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        DataCenter.HeroMedalShopDataManager:SetMedalShopData(t["medalShopArr"], t["dailyReward"], t["dailyRewardState"]);
        -- 刷新pub建筑的气泡
        EventManager:GetInstance():Broadcast(EventId.CheckPubBubble, true)
        -- 刷新英雄勋章商店
        EventManager:GetInstance():Broadcast(EventId.UpdateHeroMedalShop)
    end
end

GetHeroMedalShopInfoMessage.OnCreate = OnCreate
GetHeroMedalShopInfoMessage.HandleMessage = HandleMessage

return GetHeroMedalShopInfoMessage
