local SetWeekCardCustomRewardsMessage = BaseClass("SetWeekCardCustomRewardsMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self, id, strSelect)
    base.OnCreate(self)
    self.sfsObj:PutInt("id", id)
    self.sfsObj:PutUtfString("selectGoods", strSelect)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        DataCenter.WeekCardManager:OnRecvSelectedCustomRewards(t)
    end
end

SetWeekCardCustomRewardsMessage.OnCreate = OnCreate
SetWeekCardCustomRewardsMessage.HandleMessage = HandleMessage

return SetWeekCardCustomRewardsMessage
