--- 英雄重置协议

local HeroResetMessage = BaseClass("HeroResetMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self, heroUuid)
    base.OnCreate(self)

    self.sfsObj:PutLong("uuid", heroUuid)
end

local function HandleMessage(self, message)
    base.HandleMessage(self, message)

    if message["errorCode"] ~= nil then
        local lang = Localization:GetString(message["errorCode"])
        UIUtil.ShowTips(lang or message["errorCode"])
        return
    end

    local uuid = message["hero"]["uuid"]
    local gold = message["gold"]
    local retItems = message["retItem"]

    if message["money"] ~= nil then
        table.insert(retItems, {["id"]= ResourceType.Money, ["add"] =message["money"]})
    end
    
    LuaEntry.Player.gold = gold
    EventManager:GetInstance():Broadcast(EventId.UpdateGold)
    
    DataCenter.HeroDataManager:UpdateOneHero(message["hero"])
    EventManager:GetInstance():Broadcast(EventId.HeroResetSuccess, uuid)
    EventManager:GetInstance():Broadcast(EventId.HeroStationUpdate)
    
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIHeroResetSuccess, uuid, retItems)
end

HeroResetMessage.OnCreate = OnCreate
HeroResetMessage.HandleMessage = HandleMessage

return HeroResetMessage