local HeroPosterExchangeMessage = BaseClass("HeroPosterExchangeMessage", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self, arr)
    base.OnCreate(self)

    if arr ~= nil then
        local array = SFSArray.New()
        table.walk(arr,function (k,v)
            array:AddLong(v)
        end)
        self.sfsObj:PutSFSArray("heroArr", array)
    end
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["heroArr"] ~= nil then
            DataCenter.HeroDataManager:RemoveHeroes(t["heroArr"])
        end
        if t["goodsArr"] ~= nil then
            local message = {}
            local reward = {}
            message["reward"] = reward
            table.walk(t["goodsArr"], function (_, v)
                local tmp = {}
                tmp["type"] = RewardType.GOODS
                local value = {}
                tmp.value = value
                value["itemId"] = toInt(v.goodsId)
                value["num"] = v.addNum
                table.insert(reward, tmp)
            end)
            DataCenter.RewardManager:ShowCommonReward(message)
        end
        EventManager:GetInstance():Broadcast(EventId.PosterExchangeSuccess)
    end
end

HeroPosterExchangeMessage.OnCreate = OnCreate
HeroPosterExchangeMessage.HandleMessage = HandleMessage
return HeroPosterExchangeMessage