local PiggyBankUpdateMessage = BaseClass("PiggyBankUpdateMessage", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)

    if t["exchangeId"] then
        local pack = GiftPackageData.get(t["exchangeId"])
        if pack then
            pack._serverData.money = t.money
            if pack:isPiggyBankPack() then
                UIUtil.ShowPiggyBankTip(t)
            elseif pack:isEnergyBankPack() then
                UIUtil.ShowEnergyBankTip(t)
            end
        end
    end
end

PiggyBankUpdateMessage.OnCreate = OnCreate
PiggyBankUpdateMessage.HandleMessage = HandleMessage

return PiggyBankUpdateMessage