local PushCollectExtraRewardMessage = BaseClass("PushCollectExtraRewardMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        if t["extraReward"] then
            for k,v in pairs(t["extraReward"]) do
                DataCenter.RewardManager:AddOneReward(v)
            end
        end
    end
end

PushCollectExtraRewardMessage.OnCreate = OnCreate
PushCollectExtraRewardMessage.HandleMessage = HandleMessage

return PushCollectExtraRewardMessage