local DetectPowerUpgradeMessage = BaseClass("DetectPowerUpgradeMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)

    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode) 
    else
        DataCenter.RadarCenterDataManager:UpgradeDetectPowerInfo(t)
        EventManager:GetInstance():Broadcast(EventId.UpgradeDetectPower)
    end
end

DetectPowerUpgradeMessage.OnCreate = OnCreate
DetectPowerUpgradeMessage.HandleMessage = HandleMessage

return DetectPowerUpgradeMessage