local ReceiveHeroMedalShopDailyReward = BaseClass("ReceiveHeroMedalShopDailyReward", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self)
    base.OnCreate(self)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    local errCode =  t["errorCode"]
    if errCode ~= nil then
        UIUtil.ShowTipsId(errCode)
    else
        -- 解析奖励 奖励添加走push
        for k,v in pairs(t["reward"]) do
            DataCenter.RewardManager:AddOneReward(v)
        end
        -- 谈奖励弹窗
        DataCenter.RewardManager:ShowCommonReward(t)
        -- 同步每日奖励领取
        DataCenter.HeroMedalShopDataManager:updateDailyRewardState(t['dailyRewardState']);
        -- 刷新pub建筑的气泡
        EventManager:GetInstance():Broadcast(EventId.CheckPubBubble, true)
        -- 刷新界面每日奖励部分
        EventManager:GetInstance():Broadcast(EventId.UpdateHeroMedalShopDailyReward)
        -- 刷新界面道具数量部分
        EventManager:GetInstance():Broadcast(EventId.UpdateHeroMedalShopItem)
        
    end
end

ReceiveHeroMedalShopDailyReward.OnCreate = OnCreate
ReceiveHeroMedalShopDailyReward.HandleMessage = HandleMessage

return ReceiveHeroMedalShopDailyReward
