local UpgradeTriggerBuildingMessage = BaseClass("UpgradeTriggerBuildingMessage", SFSBaseMessage)
local base = SFSBaseMessage

function UpgradeTriggerBuildingMessage:OnCreate(param)
    base.OnCreate(self)
    if param ~= nil then
        self.sfsObj:PutInt("level", param.level)
        self.sfsObj:PutInt("trigger", param.trigger)
        if param["index"] ~= nil then
            self.sfsObj:PutInt("index", param.index)
        end
    end
end

function UpgradeTriggerBuildingMessage:HandleMessage(t)
    base.HandleMessage(self, t)
    local battleLevel = DataCenter.BattleLevel
    if battleLevel then
        battleLevel:OnUpgradeTriggerBuildingHandler(t)
    end
end

return UpgradeTriggerBuildingMessage