local UserAddDesertMineMessage = BaseClass("UserAddDesertMineMessage", SFSBaseMessage)
local base = SFSBaseMessage
local Localization = CS.GameEntry.Localization
local function OnCreate(self,uuid,serverId)
    base.OnCreate(self)
    self.sfsObj:PutLong("uuid", uuid)
    self.sfsObj:PutInt("serverId",serverId)
end

local function HandleMessage(self, t)
    base.HandleMessage(self, t)
    if t["errorCode"] ~= nil then
        UIUtil.ShowTips(Localization:GetString(t["errorCode"]))
    else
        DataCenter.DesertOperateManager:UpdateDesertMineStartTime(t)
        if t["desert"]~=nil then
            DataCenter.DesertDataManager:UpdateOneDesertData(t["desert"])
        end
        
    end
end

UserAddDesertMineMessage.OnCreate = OnCreate
UserAddDesertMineMessage.HandleMessage = HandleMessage

return UserAddDesertMineMessage