local UserSaveCrossDefendHeroMessage = BaseClass("UserSaveCrossDefendHeroMessage", SFSBaseMessage)
local base = SFSBaseMessage

local function OnCreate(self, formationUuid, heroUuids)
    base.OnCreate(self)
    
    self.sfsObj:PutLong("uuid", formationUuid)
    
    local heroArr = SFSArray.New()
    for i, heroUuid in ipairs(heroUuids) do
        local obj = SFSObject.New()
        obj:PutLong("heroUuid", heroUuid)
        obj:PutInt("index", i)
        heroArr:AddSFSObject(obj)
    end
    self.sfsObj:PutSFSArray("heroInfos", heroArr)
end

local function HandleMessage(self, message)
    base.HandleMessage(self, message)
    if message["errorCode"] then
        return
    end
    DataCenter.CrossWormManager:HandleSaveHero(message)
end

UserSaveCrossDefendHeroMessage.OnCreate = OnCreate
UserSaveCrossDefendHeroMessage.HandleMessage = HandleMessage

return UserSaveCrossDefendHeroMessage