local CharacterBaseState = require("Scene.PVEBattleLevel.Character.State.CharacterBaseState")
local CharacterBuildState = BaseClass("CharacterBuildState",CharacterBaseState)

function CharacterBuildState:OnEnter()
    self.m_owner:PlayAnim(PlayerAnimationName.Build)
    if self.m_owner.isPlayer then
        self.m_owner:ShowToolByPath("weapon/chuizi")
    end
end

function CharacterBuildState:OnExit()
