local WorldBattleSiegeEffect = BaseClass("WorldBattleSiegeEffect")
local default_show_time = 2
local Localization = CS.GameEntry.Localization
--创建
local function OnCreate(self,go)
    if go ~= nil then
        self.request = go
        self.gameObject = go.gameObject
        self.transform = go.gameObject.transform
    end
    self:ComponentDefine()
end

-- 销毁
local function OnDestroy(self)
    self:RemoveTimer()
    self:ComponentDestroy()
end

local function ComponentDefine(self)
    self.isDoAnim = false
    self.simpleAnim = self.transform:GetComponentInChildren(typeof(CS.SimpleAnimation),true)
    self.arrowTrans = self.transform:Find("num/layout").gameObject
    self.des_name = self.transform:Find("num"):GetComponent(typeof(CS.SuperTextMesh))
    self.addArrow = self.transform:Find("num/layout/add"):GetComponent(typeof(CS.UnityEngine.SpriteRenderer))
    self.addArrow.gameObject:SetActive(false)
    self.decArrow = self.transform:Find("num/layout/dec"):GetComponent(typeof(CS.UnityEngine.SpriteRenderer))
    self.decArrow.gameObject:SetActive(false)
    self.__update_handle = function() self:Update() end
    UpdateManager:GetInstance():AddUpdate(self.__update_handle)
end

local function ComponentDestroy(self)
    self.arrowTrans = nil
    self.addArrow = nil
    self.decArrow = nil
end
local function RemoveTimer(self)
    if self.__update_handle~=nil then
        UpdateManager:GetInstance():RemoveUpdate(self.__update_handle)
        self.__update_handle = nil
    end
end

local function ReInit(self,param)
    if IsNull(self.gameObject) then
        return
    end
    self.data = param
    local posV3 = SceneUtils.TileIndexToWorld(param.pointId)
    self.transform.position = posV3
    if param.state ==1 then
        self.des_name.text = Localization:GetString("110757")
        self.des_name.color32 = Color32.New(255,92,92,255)
        self.addArrow.gameObject:SetActive(false)
    else
        self.des_name.text = Localization:GetString("129030")
        self.des_name.color32 = WorldGreenColor32
        self.addArrow.gameObject:SetActive(true)
    end
    local nameWidth = self.des_name:GetWidth()
    local v3 = Vector3.New(self.des_name.gameObject.transform.position.x+(nameWidth*2/3),self.arrowTrans.transform.position.y,self.arrowTrans.transform.position.z)
    self.arrowTrans.transform.position = v3
    
    self.curTime =0
    self.isDoAnim = true
    self.simpleAnim:Play("normal")
    self.simpleAnim:PlayQueued("show")
    self:Update()
end


local function Update(self)
    if self.isDoAnim then
        self.curTime = self.curTime+Time.deltaTime
        if self.curTime > default_show_time then
            self.isDoAnim = false
            WorldBattleSiegeEffectManager:GetInstance():RemoveOneEffect(self.data.uuid)
        end
    end
end


WorldBattleSiegeEffect.OnCreate = OnCreate
WorldBattleSiegeEffect.OnDestroy = OnDestroy
WorldBattleSiegeEffect.ComponentDefine = ComponentDefine
WorldBattleSiegeEffect.ComponentDestroy = ComponentDestroy
WorldBattleSiegeEffect.Update = Update
WorldBattleSiegeEffect.RemoveTimer =RemoveTimer
WorldBattleSiegeEffect.ReInit = ReInit
return WorldBattleSiegeEffect