local UIActivitySevenDayBoxItem = BaseClass("UIActivitySevenDayBoxItem",UIBaseContainer)
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization
local ItemIcon = "ItemIcon"
local ImgQuality = "ImgQuality"
local NumText = "NumText"
local ImgRece = "ImgRece"
local path = ""
function UIActivitySevenDayBoxItem:OnCreate()
    base.OnCreate(self)
    
    self._icon_img = self:AddComponent(UIImage,ItemIcon)
    self._item_quality = self:AddComponent(UIImage, ImgQuality)
    self._num_txt = self:AddComponent(UITextMeshProUGUIEx,NumText)
    self._img_rece = self:AddComponent(UIImage,ImgRece)
    self._box_btn = self:AddComponent(UIButton,path)
    self._box_btn:SetOnClick(function()
        self:OnClickGo()
    end)
end

function UIActivitySevenDayBoxItem:OnDestroy()
   -- self._icon_img = nil
    base.OnDestroy(self)
end

function UIActivitySevenDayBoxItem:OnEnable()
    base.OnEnable(self)
end

function UIActivitySevenDayBoxItem:OnDisable()

    base.OnDisable(self)
end

function UIActivitySevenDayBoxItem:RefreshData(index,data,curscore,effect)
    self.rewardData = data
    --self.effect = effect
    --宝箱状态 0未领取,  1已领取
    self.state = self.rewardData.rewardFlag
    --当前分数
    self.curscore = curscore
    self.index = index
    self:RefreshBox()
    self:RefreshBoxState()
end

function UIActivitySevenDayBoxItem:RefreshBox()
    if not next(self.rewardData.reward) then
        return
    end
    local rewardList = DataCenter.RewardManager:ReturnRewardParamForView(self.rewardData.reward)
    table.walk(rewardList,function (k,v)
        local id = v.itemId
        local goods = DataCenter.ItemTemplateManager:GetItemTemplate(id)
        if goods~=nil then
            self._icon_img:LoadSprite(string.format(LoadPath.ItemPath,goods.icon))
            self.itemName = DataCenter.ItemTemplateManager:GetName(id)
            self.itemDesc = DataCenter.ItemTemplateManager:GetDes(id)
            self._item_quality:LoadSprite(DataCenter.ItemTemplateManager:GetToolBgByColor(goods.color))
            self._num_txt:SetText(v.count)
        end
    end)
end

--盒子状态
function UIActivitySevenDayBoxItem:RefreshBoxState()
    self._img_rece:SetActive(self.state == 1)
    if self.state == 0 then
        if self.curscore >= self.rewardData.needScore then
            --self.effect:SetActive(true)
            --self.effect:Enable(true)
        end
    end
end


function UIActivitySevenDayBoxItem:OnClickReward()
    local x = self.transform.position.x
    local y = self.transform.position.y
    local offset = 50
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIActivityRewardTip,Localization:GetString("370101"),ActivityEventType.PERSONAL,x,y,false,self.eventInfo.rewardScoreIndexArr[index],offset)
end

function UIActivitySevenDayBoxItem:OnClickGo()
    if self.state == 0 then
        if self.curscore >= self.rewardData.needScore then 
            self.view.ctrl:GetSevenDayBoxReward(self.index)
            --SFSNetwork.SendMessage(MsgDefines.UserDayActReward,self.index)
            return
        end
    end

    local param = {}
    param["itemName"] = self.itemName
    param["itemDesc"] = self.itemDesc
    param["alignObject"] = self._icon_img
    param.isLocal = true
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIItemTips,{anim = true}, param)

end

return UIActivitySevenDayBoxItem