ThemeActivityItem_Activity.ComponentDestroy = ComponentDestroy
ThemeActivityItem_Activity.DataDefine = DataDefine
ThemeActivityItem_Activity.DataDestroy = DataDestroy
ThemeActivityItem_Activity.OnEnable = OnEnable
ThemeActivityItem_Activity.OnDisable = OnDisable

ThemeActivityItem_Activity.SetItem = SetItem
ThemeActivityItem_Activity.RefreshAll = RefreshAll
ThemeActivityItem_Activity.RefreshRed = RefreshRed
ThemeActivityItem_Activity.AddTimer = AddTimer
ThemeActivityItem_Activity.SetRemainTime = SetRemainTime
ThemeActivityItem_Activity.DelTimer = DelTimer
ThemeActivityItem_Activity.OnClickJumpBtn = OnClickJumpBtn

return ThemeActivityItem_Activity