local UIAlCompeteNoticeCtrl = BaseClass("UIAlCompeteNoticeCtrl", UIBaseCtrl)

local function CloseSelf(self)
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UIAlCompeteNotice)
end

local function Close(self)
    UIManager:GetInstance():DestroyWindowByLayer(UILayer.Normal)
end

local function GetNoticeEndTime(self)
    local actInfo = DataCenter.ActivityListDataManager:GetActivityDataById(EnumActivity.AllianceCompete.ActId)
    if not actInfo then
        return
    end
    return actInfo.preOpenTime
end


UIAlCompeteNoticeCtrl.CloseSelf = CloseSelf
UIAlCompeteNoticeCtrl.Close = Close

UIAlCompeteNoticeCtrl.GetNoticeEndTime = GetNoticeEndTime

return UIAlCompeteNoticeCtrl

