local AllianceCompetePerReward = BaseClass("AllianceCompetePerReward", UIBaseContainer)
local base = UIBaseContainer
--local AllianceCompeteRewardItem = require "UI.UIAllianceCompete.UIAllianceCompeteReward.Component.AllianceCompeteRewardItem"
local MailRewardItem = require "UI.UIMailNew.UIMailMainPanel.Component.MailRewardItem"

local titleTxt_path = "rewardBg/TitleBg/Title"
local tipTxt_path = "Tip"
local content_path = "rewardBg/Rewards"


local function OnCreate(self)
    base.OnCreate(self)

    self.rewardModels ={}
    self.rewardItemsList = {}
    
    self.titleN = self:AddComponent(UIText, titleTxt_path)
    self.tipN = self:AddComponent(UIText, tipTxt_path)
    self.contentN = self:AddComponent(UIBaseContainer, content_path)
end

local function OnDestroy(self)
    self.rewardModels = nil
    self.rewardItemsList = nil
    
    self.titleN = nil
    self.tipN = nil
    self.contentN = nil
    base.OnDestroy(self)
end


local function ShowRewards(self, title, tip, rewards, hideCount)
    self.titleN:SetText(title)
    self.tipN:SetText(tip)

    local list = DataCenter.RewardManager:ReturnRewardParamForMessage(rewards)
    self:SetAllRewardsDestroy()
    self.rewardModelCount =0
    if list~=nil and #list>0 then
        for i = 1, table.length(list) do
            --复制基础prefab，每次循环创建一次
            self.rewardModelCount= self.rewardModelCount+1
            self.rewardModels[self.rewardModelCount] = self:GameObjectInstantiateAsync(UIAssets.UICommonItem, function(request)
                if request.isError then
                    return
                    
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.contentN.transform)
                go.transform:Set_localScale(0.54, 0.54, 1)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.contentN:AddComponent(MailRewardItem,nameStr)
                cell:ShowCount(hideCount)
                local param = {}
                param.itemId = list[i].itemId
                param.rewardType = list[i].rewardType
                param.count = list[i].count
                cell:RefreshData(param)
                cell:SetNameText("")
                --cell:RefreshData(list[i])
                CS.UnityEngine.UI.LayoutRebuilder.ForceRebuildLayoutImmediate(self.rectTransform)
                EventManager:GetInstance():Broadcast(EventId.AllianceCompeteRewardsReposition)
                table.insert(self.rewardItemsList,cell)
            end)
        end
    end
end


local function SetAllRewardsDestroy(self)
    self.contentN:RemoveComponents(MailRewardItem)
    if self.rewardModels~=nil then
        for k,v in pairs(self.rewardModels) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.rewardModels ={}
    self.rewardItemsList = {}
end


AllianceCompetePerReward.OnCreate = OnCreate
AllianceCompetePerReward.OnDestroy = OnDestroy


AllianceCompetePerReward.ShowRewards = ShowRewards
AllianceCompetePerReward.SetAllRewardsDestroy = SetAllRewardsDestroy

return AllianceCompetePerReward