end

local function ShowPanel(self)
    self:Init()
    local actInfo = DataCenter.ActivityListDataManager:GetActivityDataById(EnumActivity.AllianceCompete.ActId)
    if actInfo then
        SFSNetwork.SendMessage(MsgDefines.ActivityEventInfoGet,tostring(actInfo.activityId))
    end
    self.scrollRectN:SetVerticalNormalizedPosition(1)
end

local function Init(self)
    self:TextLocalization()
    self.allianceinfo = DataCenter.ActivityListDataManager:GetActivityDataById(EnumActivity.AllianceCompete.ActId)

    if DataCenter.LeagueMatchManager:CheckIsMatchOpen() and not DataCenter.LeagueMatchManager:CheckAllianceInMatch() then
        self:RefreshOpenState()
        self.rankBtn:SetActive(false)
        self.rewardBtn:SetActive(false)
        self.alRewardBtnN:SetActive(false)
        self.winRewardBtnN:SetActive(false)
        self.clock1ImgN:SetActive(false)
        self.clock2ImgN:SetActive(false)
        self.ready2Cd:SetActive(false)
        return
    else
        self.rankBtn:SetActive(true)
        self.rewardBtn:SetActive(true)
        self.alRewardBtnN:SetActive(true)
        self.clock1ImgN:SetActive(true)
        self.clock2ImgN:SetActive(true)
        self.ready2Cd:SetActive(true)
    end

    if not self.allianceinfo then
        return
    end
    if self.allianceinfo ~= nil and self.allianceinfo.finish ~= nil then
        --printInfo("联盟对决进入周日结算阶段，请求结算数据")
        SFSNetwork.SendMessage(MsgDefines.AllianceCompeteWeekResult)
        return
    end

    if self.allianceinfo.preOpenTime ~= nil then
        --预告阶段
        self.state = AllianceBatState.PreOpenState
        self:RefreshReadyState()
        --self:RefreshTime()
        return
    end
    self.eventInfo = self.allianceinfo:GetEventInfo()
    if self.eventInfo == nil then
        --printInfo("未获取积分类活动的eventInfo")
        return
    end
    --当前联盟对决活动状态
    self.state = AllianceBatState.None
    self:Refresh()
    --self:RefreshTime()
end

