local function RefreshActivityCompeteCell(self)
    --获取己方敌方联盟缩写名
    local selfAlName = ""
    local otherAlName = ""
    self.allianceInfo = DataCenter.ActivityListDataManager:GetActivityDataById(EnumActivity.AllianceCompete.ActId)
    if self.allianceInfo == nil then
        return
    end
    self.eventInfo = self.allianceInfo:GetEventInfo()
    if not self.eventInfo then
        return
    end
    local myAllianceId = LuaEntry.Player.allianceId
    table.walk(self.eventInfo.vsAllianceList, function(k, v)
        if k == myAllianceId then
            selfAlName = v.abbr
        else
            otherAlName = v.abbr
        end
    end)

    --所有需要显示的活动数据
    --没有发数据的 要隐藏cell 
    self.donate_soldier_singlecell:SetActive(false)

    --遍历
    local actList = DataCenter.AllianceCompeteDataManager:GetExtraActivityList()
    if actList ~= nil then
        for _,v in ipairs(actList) do
            local actTableData = LocalController:instance():getLine(TableName.ActivityPanel,v.activityId)
            --不同的活动对应不同的cell
            if actTableData and actTableData.type == ActivityEnum.ActivityType.DonateSoldierActivity then
                --捐兵活动
                self.donate_soldier_singlecell:SetActive(true)
                self.donate_soldier_singlecell:RefreshData(v.activityId, v.score, v.isWin, 372803, 372804, selfAlName, otherAlName)
            end
        end
    end
end

local function OnClickWinRewardBtn(self)
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIAllianceCompeteReward, { anim = true })
end


