local TabButton = BaseClass("TabButton", UIBaseContainer)
local base = UIBaseContainer

local select_path = "Selected"
local select_name_path = "Selected/SelectedTextTab"

local normal_path = "Normal"
local normal_name_path = "Normal/NormalTextTab"

local selfFlag_path = "selfFlag"

--创建
local function OnCreate(self)
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

-- 销毁
local function OnDestroy(self)
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

local function ComponentDefine(self)
    self.select = self:AddComponent(UIBaseContainer, select_path)
    self.select_name = self:AddComponent(UIText, select_name_path)

    self.normal = self:AddComponent(UIBaseContainer, normal_path)
    self.normal_name = self:AddComponent(UIText, normal_name_path)

    self.btn = self:AddComponent(UIButton, normal_path)
    self.btn:SetOnClick(function()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Button)
        self:ClickBtn()
    end)
    
    self.flag = self:AddComponent(UIBaseContainer, selfFlag_path)
    self.flag:SetActive(false)
end

local function ComponentDestroy(self)

end

local function DataDefine(self)

end

local function DataDestroy(self)

end

local function OnEnable(self)
    base.OnEnable(self)
end

local function OnDisable(self)
    base.OnDisable(self)
end

local function ReInit(self, data)
    self.data = data
    self:RefreshView()
end

local function RefreshView(self)
    if self.data == nil then
        return
    end
    self.normal:SetActive(self.data.id ~= self.currentSelect)
    self.select:SetActive(self.data.id == self.currentSelect)
    self.flag:SetActive(self.data.isSelfGroup)
    if self.data.id == self.currentSelect then
        self.select_name:SetText(self.data.name)
    else
        self.normal_name:SetText(self.data.name)
    end
end

local function ClickBtn(self)
    if self.data.callBack ~= nil and self.data.id ~= self.currentSelect then
        self.data.callBack(self.data.id)
    end
end

local function SetCurSelect(self, currentSelect)
    self.currentSelect = currentSelect
    self:RefreshView()
end

TabButton.OnCreate = OnCreate
TabButton.OnDestroy = OnDestroy
TabButton.OnEnable = OnEnable
TabButton.OnDisable = OnDisable
TabButton.ComponentDefine = ComponentDefine
TabButton.ComponentDestroy = ComponentDestroy
TabButton.DataDefine = DataDefine
TabButton.DataDestroy = DataDestroy
TabButton.ReInit = ReInit
TabButton.RefreshView = RefreshView
TabButton.ClickBtn = ClickBtn
TabButton.SetCurSelect = SetCurSelect

return TabButton