    local topOffset = self:GetTopOffset()
    
    self._anchorTransform:Set_sizeDelta(Mathf.Ceil(_size.x), anchor_sizeDelta_cy)
    self._chatShareNode.rectTransform:Set_sizeDelta(_size.x, _size.y)
